import React, {Component} from 'react';
import {connect} from 'react-redux';
import {PropTypes} from 'prop-types';
import { completeTask, selectTask } from '../actions';

class TaskList extends Component {
  render() {
    const { createdTasks, selectTask, completeTask } = this.props;
    return (
      <div>
        <ul>
          {createdTasks.map((task, index) => (
            <li 
              key={`task${task.position}`}
              onClick={ () => selectTask(index) }
              onDoubleClick={ () => completeTask(index) }
            >{task.text}</li>
          ))}
        </ul>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  createdTasks: state.tasks,
});

const mapDispatchToProps = (dispatch) => ({
  selectTask: (payload) => dispatch(selectTask(payload)),
  completeTask: (payload) => dispatch(completeTask(payload)),
});

TaskList.propTypes = {
  createdTasks: PropTypes.arrayOf(PropTypes.shape({})).isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(TaskList);
