import {
  CREATE_TASK,
  SELECT_TASK,
  COMPLETE_TASK,
  REMOVE_TASK,
  MOVE_TASK_UP,
  MOVE_TASK_DOWN,
  SAVE_TASKS,
  REMOVE_DONE_TASKS,
  REMOVE_ALL_TASKS,
} from '../actions/index';

const INITIAL_STATE = { tasks: [] };

export default function reducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case CREATE_TASK:
      return {
        ...state,
        tasks: [...state.tasks, action.payload],
      };
    case SELECT_TASK:
      console.log('Select');
      return state;
    case COMPLETE_TASK:
      console.log('Complete');
      return state;
    case REMOVE_TASK:
      return state;
    case MOVE_TASK_UP:
      return state;
    case MOVE_TASK_DOWN:
      return state;
    case SAVE_TASKS:
      return state;
    case REMOVE_DONE_TASKS:
      return state;
    case REMOVE_ALL_TASKS:
      return state;
    default:
      return state;
  }
}
